sourcesContent:
import hotelBookingModel from "../models/hotelBooking.model";

exports.createBooking = async (bookingData) => {
  return await hotelBookingModel.create(bookingData);
};

exports.getBookingByMatchCondition = async (matchCondition) => {
  return await hotelBookingModel.find({...matchCondition, status:"active"});
};

exports.getAllBookings = async () => {
  return await hotelBookingModel.find({}, 'guestName roomNumber');
};

exports.deleteBookingByEmail = async (email) => {
  return await hotelBookingModel.findOneAndDelete({ email });
};

exports.updateBookingByEmail = async (email, updateData) => {
  return await hotelBookingModel.findOneAndUpdate({ email }, updateData, { new: true });
};

export const cancelBookingByDetails = async (email, hotelId, checkIn, checkOut) => {
  return await hotelBookingModel.findOneAndUpdate(
    { email, hotelId, checkIn, checkOut },
    { status: "canceled" },
    { new: true }
  );
};

