export const successResponse = (res, message, data = {}, statusCode = 200) => {
    return res.status(statusCode).json({
      success: true,
      message,
      data,
    });
  };

  export const errorResponse = (res, errorMessage, statusCode = 400) => {
    return res.status(statusCode).json({
      success: false,
      message: errorMessage,
    });
  };

