sourcesContent:
import mongoose from "mongoose";

const RoomAllocationSchema = new mongoose.Schema({
  roomType: { 
    type: String, 
    required: true,
    trim: true
  },
  roomNumber: { 
    type: Number, 
    required: true,
    min: 1
  },
  guests: { 
    type: Number, 
    required: true,
    min: 1
  },
  pricePerNight: { 
    type: Number, 
    required: true,
    min: 0
  }
}, { _id: false });

const BookingSchema = new mongoose.Schema(
  {
    guestName: { 
      type: String, 
      required: true,
      trim: true
    },
    email: { 
      type: String, 
      required: true,
      trim: true,
      lowercase: true,
      match: [/.+\@.+\..+/, 'Please enter a valid email']
    },
    contact: { 
      type: String, 
      required: true,
      trim: true
    },
    hotelId: { 
      type: mongoose.Schema.Types.ObjectId, 
      ref: "Hotel", 
      required: true 
    }, 
    checkIn: { 
      type: Date, 
      required: true,
      // validate: {
      //   validator: function(value) {
      //     return value > new Date();
      //   },
      //   message: 'Check-in date must be in the future'
      // }
    },
    checkOut: { 
      type: Date, 
      required: true,
      validate: {
        validator: function(value) {
          return value > this.checkIn;
        },
        message: 'Check-out date must be after check-in date'
      }
    },
    guestCount: { 
      type: Number, 
      required: true,
      min: 1,
      max: 20
    },
    totalPrice: { 
      type: Number, 
      required: true,
      min: 0
    },
    status: { 
      type: String, 
      enum: ["pending", "confirmed", "checked-in", "checked-out", "cancelled"], 
      default: "confirmed" 
    },
    paymentStatus: {
      type: String,
      enum: ["pending", "paid", "refunded", "failed"],
      default: "pending"
    },
    allocatedRooms: [RoomAllocationSchema],
    specialRequests: {
      type: String,
      trim: true,
      maxlength: 500
    }
  },
  { 
    timestamps: true,
    toJSON: { virtuals: true },
    toObject: { virtuals: true }
  }
);

// Virtual for stay duration
BookingSchema.virtual('stayDuration').get(function() {
  return Math.ceil((this.checkOut - this.checkIn) / (1000 * 60 * 60 * 24));
});

// Indexes for performance
BookingSchema.index({ hotelId: 1, checkIn: 1, checkOut: 1 });
BookingSchema.index({ email: 1, status: 1 });
BookingSchema.index({ status: 1, checkIn: 1 });

// Pre-save hook to validate room allocation
BookingSchema.pre('save', function(next) {
  if (this.isModified('allocatedRooms') && this.allocatedRooms.length === 0) {
    throw new Error('At least one room must be allocated');
  }
  next();
});

// Static method to find active bookings for a hotel
BookingSchema.statics.findActiveBookings = function(hotelId, checkIn, checkOut) {
  return this.find({
    hotelId,
    status: { $in: ["confirmed", "checked-in"] },
    $or: [
      { checkIn: { $lt: checkOut }, checkOut: { $gt: checkIn } },
      { checkIn: { $gte: checkIn, $lte: checkOut } },
      { checkOut: { $gte: checkIn, $lte: checkOut } }
    ]
  });
};

const Booking = mongoose.model("Booking", BookingSchema);

export default Booking;

