import mongoose from "mongoose";

const roomSchema = new mongoose.Schema({
  roomType: { 
    type: String, 
    required: true,
    trim: true,
    unique: true
  }, 
  maxGuests: { 
    type: Number, 
    required: true,
    min: 1,
    max: 10
  }, 
  totalRooms: { 
    type: Number, 
    required: true,
    min: 1
  }, 
  pricePerNight: { 
    type: Number, 
    required: true,
    min: 0
  }, 
  availableRoomNumbers: { 
    type: [Number], 
    default: function() {
      // Generate room numbers automatically based on totalRooms
      const numbers = [];
      for (let i = 1; i <= this.totalRooms; i++) {
        numbers.push(i);
      }
      return numbers;
    },
    validate: {
      validator: function(arr) {
        return arr.length <= this.totalRooms;
      },
      message: 'Available rooms cannot exceed total rooms'
    }
  },
  amenities: {
    type: [String],
    default: []
  }
}, { _id: false });

const hotelSchema = new mongoose.Schema({
  name: { 
    type: String, 
    required: true,
    trim: true,
    unique: true
  },
  location: { 
    type: String, 
    required: true,
    trim: true
  },
  description: {
    type: String,
    trim: true,
    maxlength: 1000
  },
  rating: {
    type: Number,
    min: 0,
    max: 5,
    default: 0
  },
  rooms: [roomSchema],
  images: {
    type: [String],
    default: []
  },
  contact: {
    phone: String,
    email: String
  },
  policies: {
    cancellation: String,
    checkInTime: String,
    checkOutTime: String
  }
}, { timestamps: true });

// Pre-save hook to validate rooms
hotelSchema.pre('save', function(next) {
  if (this.rooms && this.rooms.length > 0) {
    const roomTypes = new Set();
    for (const room of this.rooms) {
      if (roomTypes.has(room.roomType)) {
        throw new Error(`Duplicate room type: ${room.roomType}`);
      }
      roomTypes.add(room.roomType);
    }
  }
  next();
});

// Method to get available rooms
hotelSchema.methods.getAvailableRooms = function(checkIn, checkOut) {
  return this.rooms.map(room => {
    const availableCount = room.availableRoomNumbers.length;
    return {
      roomType: room.roomType,
      maxGuests: room.maxGuests,
      pricePerNight: room.pricePerNight,
      availableCount,
      available: availableCount > 0
    };
  });
};

const Hotel = mongoose.model("Hotel", hotelSchema);

export default Hotel;

